mapper.select:
select * from (select rownum as rnum, a.* from (select * from income where nickname = #{nickname}
and
regdate like #{year}||'-'||#{month}||'-'||#{day}
regdate like #{year}||'-'||#{month}||'%'
regdate like #{year}||'%'
regdate like '%-'||#{month}||'-%'
order by regdate desc) a) where rnum between #{start} and #{end}

select regdate, count(regdate) as count from income
	where nickname = #{value}
	group by regdate

mapper.update:
update income set comments = #{comments}, ways=#{ways},
				  regdate=#{regdate}, memo=#{memo}, price=#{price}
				  where idx = #{idx}
