mapper.select:
select * from (select rownum as rnum, a.* from (select * from income
where regdate like #{year}||'-'||#{month}||'-'||#{day}
where regdate like #{year}||'-'||#{month}||'%'
where regdate like #{year}||'%'
where regdate like '%-'||#{month}||'-%'
order by regdate desc) a) where rnum between #{start} and #{end} and nickname = #{nickname}

mapper.update:
update income set comments = #{comments}, ways=#{ways},
				  regdate=#{regdate}, memo=#{memo}, price=#{price}
				  where idx = #{idx}
