mapper.select:
select * from (select rownum as rnum, a.* from (select * from expand
where regdate like #{year}||'-'||#{month}||'-'||#{day}
where regdate like #{year}||'-'||#{month}||'%'
where regdate like #{year}||'%'
where regdate like '%-'||#{month}||'-%'
order by regdate desc) a ) where rnum between #{start} and #{end} and nickname = #{nickname}

mapper.update:
update expand set comments = #{comments}, regdate = #{regdate}, big_ways = #{big_ways},
				  small_ways = #{small_ways}, big_purpose = #{big_purpose}, 
				  small_purpose = #{small_purpose}, price = #{price}, memo = #{memo}
	where idx = #{idx}
